mapper.select:
SELECT
	count(1)
FROM
	`distribution_cash` distributionCash


  		AND `status` = #{status.ordinal}


  		AND `bank` = #{bank}


  		AND `account` = #{account}


  		AND `accountHolder` = #{accountHolder}


  		AND distributor_id = #{distributor.id}

SELECT IFNULL(sum(amount), 0) FROM `distribution_cash` distributionCash WHERE `status` = 1
AND created_date >= #{beginDate}
AND created_date <= #{endDate}

		SELECT

		FROM
			`distribution_cash` distributionCash
		LEFT JOIN `distributor` distributor ON distributionCash.`distributor_id` = distributor.`id`
		LEFT JOIN `users` member ON distributor.`member_id` = member.`id`
		WHERE
			`dtype` = 'Member'

  			AND `status` = #{status.ordinal}


	  		AND `bank` = #{bank}


	  		AND `account` = #{account}


	  		AND `accountHolder` = #{accountHolder}


	  		AND distributor_id = #{distributor.id}


			AND 

		${ew.sqlSegment}

mapper.update:
    	UPDATE distribution_cash
		SET last_modified_date = #{entity.lastModifiedDate},
			version = #{entity.version} + 1,
			`status` = #{entity.status}
		WHERE
			id = #{entity.id}
		AND version = #{entity.version}
