mapper.select:
SELECT
FROM `sku` sku
LEFT JOIN `promotion_default_attribute_sku` ON id = gifts_id WHERE gift_attributes_id = #{attributeValue}
WHERE ${attributeName} = #{attributeValue}

SELECT

FROM
	`sku` sku


		AND sku.store_id = #{store.id}


		AND sku.type = #{type.ordinal}


		AND sku.id NOT IN

        	#{match.id}



		LIMIT 0, ${count}

	SELECT

	FROM
		`sku` sku
	LEFT JOIN product product ON sku.product_id = product.id
	WHERE
		(sku.sn LIKE concat('%', #{keyword}, '%') OR product.name LIKE concat('%', #{keyword}, '%'))

		AND product.store_id = #{store.id}


		AND product.type = #{type.ordinal}


		AND sku.id NOT IN

        	#{exclude.id}



		LIMIT 0, ${count}
